package crazyjava;

import java.io.BufferedReader;
import java.io.FileInputStream;
import java.io.IOException;
import java.io.InputStreamReader;
import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetAddress;
import java.net.SocketException;

public class WebIpnSendUdp {

	public static void main(String[] args) throws IOException {
		ReceiveUdps demo1 = new ReceiveUdps();
		demo1.start();
		SendUdp();
		
	}
	
	public static void getOwn() throws IOException
	{
		InetAddress address = InetAddress.getLocalHost();
		System.out.println("Local ip is:"+address.getHostAddress());
		System.out.println("Local ip is:"+address.getHostName());
		
		InetAddress address1 = InetAddress.getByName("192.168.123.23");
		System.out.println("Local ip is:"+address1.getHostAddress());
		System.out.println("Local ip is:"+address1.getHostName());
	}
	
	public static void SendUdp() throws IOException
	{
		DatagramSocket sock = new DatagramSocket();
		
		for(int i=0;i<10;i++)
		{
			String data = i + "";
			DatagramPacket datPacket = new DatagramPacket(data.getBytes(), data.getBytes().length, InetAddress.getLocalHost(), 9090);
			sock.send(datPacket);
		}
		sock.close();
	}	
}

class ReceiveUdps extends Thread
{

	@Override
	public void run() {
		DatagramSocket datagramsock = null;
		try {
			datagramsock = new DatagramSocket(9090);
		} catch (SocketException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		byte[] buf = new byte[1024*16];
		DatagramPacket datagrampak = new DatagramPacket(buf, buf.length);
		Boolean flag = true;
		try {
			while(flag)
			{
				datagramsock.receive(datagrampak);
				String word = new String(buf,0,datagrampak.getLength());
				if(word.equals("")==false)
				{
					System.out.println("The received info: "+ word);
				}
				else
				{
					flag = false;
				}
				Thread.sleep(50);
			}
			datagramsock.close();
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (InterruptedException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
	}
}


